#!/usr/bin/env node

/**
 * Post-build script for Angular + Cloudflare Pages
 *
 * Tasks:
 * 1. Copy browser/* files to dist/web/ root (required by Cloudflare Pages)
 * 2. Create _redirects for SPA routing
 * 3. Create _headers for security & cache
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

const distWebPath = path.join(__dirname, '../../dist/web');
const browserPath = path.join(distWebPath, 'browser');
const distPath = browserPath;

// _redirects - Read from public/_redirects (single source of truth)
// This ensures SPA routing rules are always in sync with the source
const publicRedirectsPath = path.join(__dirname, '../../public/_redirects');
let redirectsContent;

if (fs.existsSync(publicRedirectsPath)) {
  redirectsContent = fs.readFileSync(publicRedirectsPath, 'utf8');
  console.log('üìã Using _redirects from public/ directory (single source of truth)');
} else {
  // Fallback: minimal SPA routes if public/_redirects doesn't exist
  console.warn('‚ö†Ô∏è  Warning: public/_redirects not found, using minimal fallback');
  redirectsContent = `# Fallback SPA routing (generated by create-cloudflare-config.js)
# Prefer editing public/_redirects instead.

/auth/* /index.html 200
/cars/* /index.html 200
/bookings/* /index.html 200
/wallet/* /index.html 200
/profile/* /index.html 200
/admin/* /index.html 200

# NOTE: No catch-all. 404.html handles unknown routes.
# This prevents stale JS chunks from being served as HTML (200),
# which caused zombie Service Workers.
`;
}

// _headers - Read from public/_headers (single source of truth)
// This ensures CSP and other headers are always in sync
const publicHeadersPath = path.join(__dirname, '../../public/_headers');
let headersContent;

if (fs.existsSync(publicHeadersPath)) {
  headersContent = fs.readFileSync(publicHeadersPath, 'utf8');
  console.log('üìã Using _headers from public/ directory (single source of truth)');
} else {
  // Fallback headers if public/_headers doesn't exist
  console.warn('‚ö†Ô∏è  Warning: public/_headers not found, using fallback');
  headersContent = `# Headers globales de seguridad
/*
  X-Frame-Options: DENY
  X-Content-Type-Options: nosniff
  X-XSS-Protection: 1; mode=block
  Referrer-Policy: strict-origin-when-cross-origin
  Permissions-Policy: geolocation=(self), microphone=(), camera=(self), payment=(self), usb=(), magnetometer=(), gyroscope=(), accelerometer=()
  Strict-Transport-Security: max-age=31536000; includeSubDomains; preload
  Content-Security-Policy: default-src 'self'; script-src 'self' 'unsafe-inline' 'unsafe-eval' https://*.googletagmanager.com https://www.googletagmanager.com https://*.google-analytics.com https://sdk.mercadopago.com https://*.mercadopago.com https://*.mercadolibre.com https://*.mlstatic.com https://api.mapbox.com https://unpkg.com https://www.gstatic.com https://ajax.googleapis.com https://static.cloudflareinsights.com https://connect.facebook.net https://*.facebook.com https://*.facebook.net https://graph.facebook.com https://accounts.google.com; style-src 'self' 'unsafe-inline' https://fonts.googleapis.com https://api.mapbox.com https://unpkg.com https://*.mlstatic.com https://api.fontshare.com https://accounts.google.com; img-src 'self' data: https: blob: https://i.pravatar.cc https://*.gravatar.com https://*.facebook.com https://platform-lookaside.fbsbx.com https://*.googleusercontent.com; font-src 'self' data: https://fonts.gstatic.com https://unpkg.com https://*.mlstatic.com https://cdn.fontshare.com; media-src 'self' blob:; connect-src 'self' blob: data: https://*.sentry.io https://*.supabase.co wss://*.supabase.co https://*.mercadopago.com https://api.mercadopago.com https://events.mercadopago.com https://*.mercadolibre.com https://*.mlstatic.com https://*.workers.dev https://*.run.app wss://*.run.app https://api.mapbox.com https://events.mapbox.com https://*.mapbox.com https://sql.mapbox.com https://*.googletagmanager.com https://www.googletagmanager.com https://*.google-analytics.com https://www.google-analytics.com https://*.analytics.google.com https://region1.google-analytics.com https://stats.g.doubleclick.net https://www.googleapis.com https://generativelanguage.googleapis.com wss://generativelanguage.googleapis.com https://api.binance.com https://data-api.binance.vision https://parallelum.com.br https://unpkg.com https://www.gstatic.com https://fonts.gstatic.com https://api.unsplash.com https://images.unsplash.com https://ui-avatars.com https://ipapi.co https://placehold.co https://api.fontshare.com https://cdn.fontshare.com https://cloudflareinsights.com https://static.cloudflareinsights.com https://graph.facebook.com https://connect.facebook.net https://i.pravatar.cc https://*.gravatar.com https://accounts.google.com https://oauth2.googleapis.com; frame-src 'self' https://*.mercadopago.com https://*.mercadopago.com.ar https://mercadopago.com.ar https://*.mercadolibre.com https://sdk.mercadopago.com https://calendar.google.com https://*.facebook.com https://web.facebook.com https://accounts.google.com; worker-src 'self' blob: https://www.gstatic.com; object-src 'none'; base-uri 'self'; form-action 'self' https://*.mercadopago.com https://accounts.google.com;

# Cache para assets con hash (1 a√±o)
/*.js
  Cache-Control: public, max-age=31536000, immutable
  Content-Type: application/javascript; charset=utf-8

/*.css
  Cache-Control: public, max-age=31536000, immutable
  Content-Type: text/css; charset=utf-8

# Cache para im√°genes
/*.jpg
  Cache-Control: public, max-age=31536000, immutable
/*.jpeg
  Cache-Control: public, max-age=31536000, immutable
/*.png
  Cache-Control: public, max-age=31536000, immutable
/*.svg
  Cache-Control: public, max-age=31536000, immutable
/*.webp
  Cache-Control: public, max-age=31536000, immutable

# Cache para fuentes
/*.woff2
  Cache-Control: public, max-age=31536000, immutable
  Content-Type: font/woff2
/*.woff
  Cache-Control: public, max-age=31536000, immutable
  Content-Type: font/woff

# Sin cache para index.html y manifest
/index.html
  Cache-Control: public, max-age=0, must-revalidate
/manifest.webmanifest
  Cache-Control: public, max-age=0, must-revalidate

# CRITICAL: env.json/env.js must NEVER be cached - contains runtime configuration
# This ensures all users always get fresh environment variables
/env.json
  Cache-Control: no-store, no-cache, must-revalidate, proxy-revalidate, max-age=0
  Pragma: no-cache
  Expires: 0
/env.js
  Cache-Control: no-store, no-cache, must-revalidate, proxy-revalidate, max-age=0
  Pragma: no-cache
  Expires: 0
`;
}

// Step 1: Skip copying files (we will deploy the browser directory directly)
// Angular builds to dist/web/browser/

// Step 2: Escribir archivos de configuraci√≥n en el directorio browser
// As√≠ podemos desplegar dist/web/browser directamente y tener todo junto
fs.writeFileSync(path.join(browserPath, '_redirects'), redirectsContent);
fs.writeFileSync(path.join(browserPath, '_headers'), headersContent);

console.log('‚úÖ Archivos de configuraci√≥n de Cloudflare Pages creados en dist/web/browser:');
console.log('   - _redirects (SPA routing)');
console.log('   - _headers (Security & Cache)');

// Step 3: Rename index.csr.html to index.html if needed
// Angular with outputMode: server generates index.csr.html
const indexCsrPath = path.join(browserPath, 'index.csr.html');
const indexPath = path.join(browserPath, 'index.html');

if (fs.existsSync(indexCsrPath) && !fs.existsSync(indexPath)) {
  fs.renameSync(indexCsrPath, indexPath);
  console.log('‚úÖ Renamed index.csr.html to index.html for static deployment');
}
